import React from "react";
import styles from './AllTopics.module.css'
// aceste 2 functii le folosim sa accesam state si actions din store
import { useSelector, useDispatch } from "react-redux";
import { selectFilteredAllTopics } from "./allTopicsSlice";
import { addPost, fetchPostsbyTopic } from "./postsSlice";


// aici afisam toate topics fetched by reedit.com
// cand apasam butonul ce reprezinta un topic, sa imi dispatch action fetchPostsbyTopic
function AllTopics (){
    const dispatch = useDispatch();
    const allTopics = useSelector(selectFilteredAllTopics);
    const { isLoading } = useSelector((state) => state.allTopics);
    console.log("all topics:");
    console.log(allTopics);



    return(
     <div className={styles.topicsContainer}>
        <h2>Topics</h2>
        <ul>
            {allTopics.map((topic)=>(
                 <li key={topic.id}>
                    <button type="button" onClick={onClickHandler}>
                        {topic.display_name}
                    </button>
                </li>
            ))}
        </ul>
     </div>

    );
}

export default AllTopics;
