import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

// creem slice
// creem asyncThunk pt request la pagina 
const fetchTopics = createAsyncThunk(
    'allTopics/fetchTopics', 
    async () =>{
        // async func ce face a reguest la reedit, intoarce lista de topics
    }
)


export const allTopicsSlice = createSlice({
    name: 'allTopics',
    initialState:{
        topics: [],
        isLoading: false,
        hasError: false
    },
    reducers: {},
    extraReducers: {
        [fetchTopics.pending]:(state, action) =>{
            state.isLoading = true;
            state.hasError = false;
        },
        [fetchTopics.fulfilled]:(state, action)=>{
            state.topics.push(action.payload);
            state.isLoading= false;
            state.hasError = false;
        },
        [fetchTopics.rejected]:(state, action)=>{
            state.isLoading = false;
            state.hasError = true;
        }

    }

});


// creem selectors ca apoi sa folosim state si action in componenta(AllTopics.js) care afiseaza app
// acolo vom folosi useSelector(), useDispatch()

export default allTopicsSlice.reducer;
