import {createAsyncThunk, createSlice} from '@reduxjs/toolkit';
import { createSelector } from '@reduxjs/toolkit';

//asyncThunk action care ne da posts pt subredit(topic) specific
// asyncThunk func that fetch posts by topic 
export const fetchPostsbyTopic = createAsyncThunk(
  'posts/getPostsbyTopic',
  async(topic) =>{
    const baseUrl = 'https://www.reddit.com';

    try{
      // topic va fi ce am ales eu apasand butonul
      // imi va lua url asociat topicului: ex home: /r/Home/
      const response = await fetch(`${baseUrl}${topic}.json`);
      if(response.ok){
        const jsonResponse = await response.json();

        let posts = [];
        posts = jsonResponse.data.children.map((post)=> post.data);
        console.log("posts:");
        console.log(posts);
        return posts;
      }
    }catch (error){
      console.log(error);
    }
  }
)


export const postsSlice = createSlice({
  name: 'posts',
  initialState:{
    posts: [],
    isLoading: false,
    hasError: false,
    selectedTopic: '/r/Home/',
    searchTerm: ''
  },
  reducers: {
    setSelectedTopic: (state, action) =>{
      state.selectedTopic = action.payload;
    },
    setSearchTerm:(state, action) =>{
    state.searchTerm = action.payload;
    }
  },
  extraReducers:{
    [fetchPostsbyTopic.pending]:(state, action)=>{
      state.isLoading = true;
      state.hasError = false;
    },
    [fetchPostsbyTopic.fulfilled]:(state, action)=>{
      state.posts = action.payload;
      state.isLoading = false;
      state.hasError = false;
    },
    [fetchPostsbyTopic.rejected]:(state, action)=>{
      state.isLoading = false;
      state.hasError = true;
    }
  }

})



// creem selectors ca apoi sa folosim state si action in componenta(Posts.js) 
// acolo vom folosi accesa valorile din state cu useSelector(), useDispatch()
// .topic state selector
export const selectSelectedTopic =(state)=>state.posts.selectedTopic;
export const selectFilteredSelectedTopic= (state) =>{
  const selectedTopic = selectSelectedTopic(state);
  return selectedTopic; 
}
// .hasError state selector
export const selectHasError =(state)=>state.posts.hasError;
export const selectFilteredHasError =(state)=>{
  const hasError = selectHasError(state);
  return hasError;
}

// .searchTerm state selector
export const selectSearchTerm = (state) => state.posts.searchTerm;
export const selectFilteredSearchTerm= (state) =>{
  const searchTerm = selectSearchTerm(state);
  return searchTerm;
}

// daca nu am pus topic inca, nu pot cere posts ca si la celelalte, pt ca depinde de topic
// trebuie sa folosesc createSelector care asteapta dupa alt state
// .posts state selector
export const selectPosts = (state)=>state.posts.posts;
//export const selectFilteredPosts = createSelector([selectPosts], posts => posts);

export const selectFilteredPosts = createSelector(
  [selectPosts, selectSearchTerm],
  (posts, searchTerm) => {
    if (searchTerm !== '') {
      return posts.filter((post) =>
        post.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    return posts;
  }
);


// export action an reducer
export const {setSelectedTopic, setSearchTerm} = postsSlice.actions;
export default postsSlice.reducer;
