import { configureStore } from '@reduxjs/toolkit';
import allTopicsReducer from '../features/allTopicsSlice';
import postsReducer from '../'

export const store = configureStore({
  reducer: {
   allTopics: allTopicsReducer,
   posts: postsReducer
  },
});
