import React from "react";
import styles from './Header.module.css'
// in terminal:  npm install react-icons
// de pe pagina de react-icons copiem icon 
import { BsSearch } from "react-icons/bs";
import { BsReddit } from 'react-icons/bs';
import { useDispatch } from "react-redux";
import { setSearchTerm } from "./postsSlice";


// primeste prop de la parinte 
// cand apas pe search button sa-mi seteze searchInput state cu ce am tastat
// postsSlice manage state la searchInput
function Header() {
  const dispatch = useDispatch();

  function handleChangeInput(e){
    e.preventDefault();
    // setSearchInput este action din postsSlice
    dispatch(setSearchTerm(e.target.value)); //schimba state la searchInput
  }
    return (
        <div className={styles.header}>  
          <div className={styles.logoDiv} >
            <BsReddit  size={28} className={styles.redditIcon}/>
            <p className={styles.logoName}><span className={styles.span}>Reddit</span>myVersion</p>
          </div>
          <form className={styles.searchForm}>
            <input 
            type="text"
            placeholder="Search"
            id="search"
            onChange={handleChangeInput}
            />
            <button type="submit"  aria-label="Search">
              <BsSearch size={22} className={styles.searchIcon}/>
            </button>
          </form>
        </div>
    );
}

export default Header;
