import React, { useEffect } from "react";
import styles from './Posts.module.css'
import { useSelector, useDispatch } from "react-redux";
import { 
     selectFilteredSelectedTopic,
     fetchPostsbyTopic,
     selectFilteredPosts
    } from "./postsSlice";

function Posts (){
    const dispatch = useDispatch();
   //let selectedTopic = useSelector(selectFilteredSelectedTopic);
   const selectedTopic = '/r/Home/';
   console.log("selected topic:");
   console.log(selectedTopic); 
   ;
   
   // cand apas pe buton se schimba state la selectedTopic
   // in functie de aceasta variabila, useEffect afiseaza apoi posts
   useEffect(()=>{
    dispatch(fetchPostsbyTopic(selectedTopic));
    let posts =[];
    posts = useSelector(selectFilteredPosts);
    console.log("State posts:");
    console.log(posts);
   });

    
    return(
        <div className={styles.postsContainer}>
          {posts}
        </div>
       );
   
}

export default Posts;


