import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";




// creem slice
// creem asyncThunk pt request la pagina 
export const fetchTopics = createAsyncThunk(
    'allTopics/fetchTopics', 
    async () =>{
        /*
        // async func ce face a reguest la reedit, intoarce  promise:lista de topics
        const app_id= "POOKPeVYwOCLBLoH0vPsGQ";
        // const secret_key ="XJco84Bb2E5Mtjuwr4-hZpiMCQIzaA";
         const redirect_uri ="http://localhost:3000/";
        const baseUrl = `https://www.reddit.com/api/v1/authorize?client_id=${app_id}&response_type='code'&state='state'&redirect_uri=${redirect_uri}&duration='permanent'&scope='mysubreddits'/subreddits/mine/subscriber&raw_json=1`;
         // &raw_json=1
         // /subreddits/mine/subscriber ???

         const response = await fetch(baseUrl, {mode: 'no-cors'});
        */ 
         const baseUrl = 'https://www.reddit.com';
         try{
           const response = await fetch(`${baseUrl}/subreddits.json`);
           if(response.ok){
               const jsonResponse = await response.json();
               console.log("raspunsul:")
               let topics = [];
               topics = jsonResponse.data.children.map((subreddit) => subreddit.data);
               return topics;
               
           }
       
         }catch(error){
           console.log(error);
         }
    }
       
)


export const allTopicsSlice = createSlice({
    name: 'allTopics',
    initialState:{
        topics: [],
        isLoading: false,
        hasError: false
    },
    reducers: {},
    extraReducers: {
        [fetchTopics.pending]:(state, action) =>{
            state.isLoading = true;
            state.hasError = false;
        },
        [fetchTopics.fulfilled]:(state, action)=>{
            state.topics= action.payload;
            state.isLoading= false;
            state.hasError = false;
        },
        [fetchTopics.rejected]:(state, action)=>{
            state.isLoading = false;
            state.hasError = true;
        }

    }

});


// creem selectors ca apoi sa folosim state si action in componenta(AllTopics.js) 
// acolo vom folosi useSelector(), useDispatch()

export const selectAllTopics =(state)=>state.allTopics.topics;
export const selectFilteredAllTopics =(state)=>{
    const topics = selectAllTopics(state);
    return topics;
}

export default allTopicsSlice.reducer;
