import React, { useEffect } from "react";
import styles from './Posts.module.css'
import { useSelector, useDispatch } from "react-redux";
//import { fetchPostsbyTopic, selectFilteredPosts, selectFilteredSelectedTopic } from "./postsSlice";
import { selectFilteredSelectedTopic, selectFilteredPosts, fetchPostsbyTopic, selectSelectedTopic } from "./postsSlice";

function Posts (){
   
   const dispatch = useDispatch();
   
   const posts = useSelector(selectFilteredPosts);
   console.log("al posts:");
   console.log(posts);
   /*
   let selectedTopic = useSelector(selectFilteredSelectedTopic);
   console.log("selected topic:");
   console.log(selectedTopic); 
   let posts = useSelector(selectFilteredPosts);
   console.log("Posts:");
   console.log(posts);

 */
   // cand apas pe buton se schimba state la selectedTopic
   // in functie de aceasta variabila, useEffect afiseaza apoi posts
   /*
   useEffect(()=>{
    dispatch(fetchPostsbyTopic('/r/Home/'));
   },[dispatch])
   */
    
   
    if(posts.length !==0){
        return(
            <div className={styles.postsContainer}>
              {posts.map((post)=>(
                <div key={post.id}>
                    <p>{post.author}</p>
                </div>
              ))}
            </div>
           );

    }
    

   
}

export default Posts;
