import React ,{ useEffect }from 'react';
import styles from './App.module.css'
import Header from './features/Header';
import AllTopics from './features/AllTopics';
import Posts from './features/Posts';
import { fetchTopics } from './features/allTopicsSlice';
import { useDispatch} from 'react-redux';

// app renders toata aplicatia, inclusiv allTopics
// aici trebuie useEffect, pt ca dupa prima render, sa imi afiseze action allTopics(fetch)
// aplicatia trebuie sa stie cand afectueaza fetch (la useEffect). cand eu deschid aplicatia, datorita
// la useEffect, se incarca deja topics, fara sa apas eu un buton de ex
function App() {

const express = require("express")
const app = express()
const cors = require("cors")

app.use(cors())

  const dispatch = useDispatch();

  useEffect(()=> {
    dispatch(fetchTopics());
  },[dispatch])

  return (
   <div className={styles.div}>
    <Header/>
    <div className={styles.topics_postsContainer}> 
      <AllTopics/>
      <Posts/>
    </div>
   </div>

  );
}

export default App;
