import React, { useEffect } from "react";
import styles from './Posts.module.css'
import { useSelector, useDispatch } from "react-redux";
import { 
     selectFilteredSelectedTopic,
     fetchPostsbyTopic,
     selectFilteredPosts,
     selectFilteredHasError,
    } from "./postsSlice";

function Posts (){
    const dispatch = useDispatch();
    let hasError = useSelector(selectFilteredHasError);
    const posts = useSelector(selectFilteredPosts);
    let selectedTopic = useSelector(selectFilteredSelectedTopic);
    const topic = '/r/Home/';
    console.log(" topic:");
    console.log(topic); 
   
   // cand apas pe buton se schimba state la selectedTopic
   // in functie de aceasta variabila, useEffect afiseaza apoi posts
    useEffect(()=>{
     dispatch(fetchPostsbyTopic(topic));   
     console.log("din effect");
    });
    
    
    if(hasError){
        return(
            <div>
                <h2>Failed to load posts</h2>
            </div>
        )
    }
    return(
       
        <div className={styles.postsContainer}>
         
        </div>
   
   
       );
   
}

export default Posts;
