import React from "react";
import styles from './Header.module.css'
// in terminal:  npm install react-icons to use React icons
// from the page, copy the icon
import { BsReddit } from 'react-icons/bs';
import { useDispatch } from "react-redux";
import { setSearchTerm } from "./postsSlice";

// react component that displays the header of the application
function Header() {
  const dispatch = useDispatch();

  function handleChangeInput(e){
    e.preventDefault();
    // setSearchTerm este action din postsSlice care schimba state la searchTerm
    // use the action setSearch
    dispatch(setSearchTerm(e.target.value)); 
  }
    return (
        <div className={styles.header}>  
          <div className={styles.logoDiv} >
            <BsReddit  size={28} className={styles.redditIcon}/>
            <p className={styles.logoName}><span className={styles.span}>Reddit</span>myVersion</p>
          </div>
          <form className={styles.searchForm}>
            <input 
            type="text"
            placeholder="Search"
            id="search"
            onChange={handleChangeInput}
            />
          </form>
        </div>
    );
}

export default Header;
