import React, { useEffect } from "react";
import styles from './Posts.module.css'
import { useSelector, useDispatch } from "react-redux";
import { 
     selectFilteredSelectedTopic,
     fetchPostsbyTopic,
     selectFilteredPosts,
     selectFilteredHasError,
    } from "./postsSlice";

function Posts (){
    const dispatch = useDispatch();
    let hasError = useSelector(selectFilteredHasError);
    const posts = useSelector(selectFilteredPosts);
    const topic= useSelector(selectFilteredSelectedTopic);
   
   // cand apas pe buton se schimba state la selectedTopic
   // in functie de aceasta variabila, useEffect afiseaza apoi posts
    useEffect(()=>{
     dispatch(fetchPostsbyTopic(topic));   
    },[topic]);
    
    
    if(hasError){
        return(
            <div>
                <h2>Failed to load posts</h2>
            </div>
        )
    }
    return(
       
        <div className={styles.postsContainer}>
            {posts.map((post)=>(
              <div key ={post.id}>
                <p>{post.subreddit}</p>
                <p>{post.title}</p>
                <p>{post.author}</p>
                <img src={post.url} alt= ''/>
            </div>
           ))}  
         
        </div>
   
   
       );
   
}

export default Posts;
