import React, { useEffect } from "react";
import styles from './Posts.module.css'
import { useSelector, useDispatch } from "react-redux";
import { 
     selectFilteredSelectedTopic,
     fetchPostsbyTopic,
     selectFilteredPosts,
     selectFilteredHasError,
    } from "./postsSlice";

function Posts (){
    const dispatch = useDispatch();
    const hasError = useSelector(selectFilteredHasError);
    const topic= useSelector(selectFilteredSelectedTopic);
    let posts = useSelector(selectFilteredPosts);
    console.log(hasError);
    console.log("merge?:");
    console.log(topic);
    console.log(posts);
   
   // cand apas pe buton se schimba state la selectedTopic
   // in functie de aceasta variabila, useEffect afiseaza apoi posts
    useEffect(()=>{
      dispatch(fetchPostsbyTopic(topic));   
    },[topic]);
    
    
    if(hasError){
        return(
            <div>
                <h2>Failed to load posts</h2>
            </div>
        )
    }
    return(
       
        <div >
            {posts.map((post)=>(
            <div key ={post.id}>
                <h2>{post.subreddit}</h2>
                <p className={styles.author}>r/{post.author}</p>
                <h3>{post.title}</h3>
                <img src={post.url}/>
            </div>
           ))}  
        </div>
   
   
       );
   
}

export default Posts;
