import React, { useEffect } from "react";
import styles from './Posts.module.css'
import { useSelector, useDispatch } from "react-redux";
import { 
     selectFilteredSelectedTopic,
     fetchPostsbyTopic,
     selectFilteredPosts,
     selectFilteredHasError,
    } from "./postsSlice";


// react componetn that displays the posts by the topic selected
function Posts (){
    const dispatch = useDispatch();
    const hasError = useSelector(selectFilteredHasError);
    const topic= useSelector(selectFilteredSelectedTopic);
    let posts = useSelector(selectFilteredPosts);

   // when a button (topic) is clicked, the state of selectedTopic from postsSlice changes
   // useEffect displays the posts by topic selected (selectedTopic)
    useEffect(()=>{
      dispatch(fetchPostsbyTopic(topic));   
    },[topic]);
    
    
    if(hasError){
        return(
            <div>
                <h2>Failed to load posts</h2>
            </div>
        )
    }
    
    // if the length of posts array = 0, no match were fund and display a
    if (posts.length === 0){
        return(
            <p>No results founded.</p>
        )
    }
    return(
       
        <div >
            {posts.map((post)=>(
            <div key ={post.id} className={styles.postContainer}>
                <h2>{post.subreddit}</h2>
                <p className={styles.author}>r/{post.author}</p>
                <h3>{post.title}</h3>
                <img src={post.url}/>
            </div>
           ))}  
        </div>
   
   
       );
   
}

export default Posts;
