function Posts (){
   
   const dispatch = useDispatch();
   const selectedTopic = useSelector(selectSelectedTopic);
   console.log("selected topic:");
   console.log(selectedTopic); 
   //const posts = useSelector(selectFilteredPosts);
   //console.log("Posts:");
   //console.log(posts);


   // cand apas pe buton se schimba state la selectedTopic
   // in functie de aceasta variabila, useEffect afiseaza apoi posts
   useEffect(()=>{
    dispatch(fetchPostsbyTopic('selectedTopic'));
   })

    return(
     <div className={styles.postsContainer}>
      
     </div>
    );
}

export default Posts;
