import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

// creem slice
// creem asyncThunk pt request la pagina 
export const fetchTopics = createAsyncThunk(
    'allTopics/fetchTopics', 
    async () =>{
        // async func ce face a reguest la reedit, intoarce a promise:lista de topics
    }
)


export const allTopicsSlice = createSlice({
    name: 'allTopics',
    initialState:{
        topics: [],
        isLoading: false,
        hasError: false
    },
    reducers: {},
    extraReducers: {
        [fetchTopics.pending]:(state, action) =>{
            state.isLoading = true;
            state.hasError = false;
        },
        [fetchTopics.fulfilled]:(state, action)=>{
            state.topics= action.payload;
            state.isLoading= false;
            state.hasError = false;
        },
        [fetchTopics.rejected]:(state, action)=>{
            state.isLoading = false;
            state.hasError = true;
        }

    }

});


// creem selectors ca apoi sa folosim state si action in componenta(AllTopics.js) 
// acolo vom folosi useSelector(), useDispatch()
/*
export const selectAllTopics =(state)=>state.allTopics.topics;
export const selectFilteredAllTopics =(state)=>{
    const topics = selectAllTopics(state);
    return topics;
}
*/
export default allTopicsSlice.reducer;
