import {createAsyncThunk, createSlice} from '@reduxjs/toolkit';

// creem slice
// creem asyncThunk action care ne da post pt subredit specific
export const fetchPostsbyTopic = createAsyncThunk(
  'posts/getPostsbyTopic',
  async(topic) =>{
    const baseUrl = 'https://www.reddit.com';

    try{
      // topic va fi ce am ales eu apasand butonul
      // imi va lua url asociat topicului: ex home: /r/Home/
      const response = await fetch(`${baseUrl}${topic}.json`);
      if(response.ok){
        const jsonResponse = await response.json();

        let posts = [];
        posts = jsonResponse.data.children.map((post)=> post.data);
        console.log("posts:");
        console.log(posts);
        return posts;

      }
    }catch (error){
      console.log(error);
    }
  }
)


export const postsSlice = createSlice({
  name: 'posts',
  initialState:{
    posts: [],
    isLoading: false,
    hasError: false,
    selectedTopic: '/r/Home/',
  },
  reducers: {
    setSelectedTopic: (state, action) =>{
      state.selectedTopic = action.payload;
    }
  },
  extraReducers:{
    [fetchPostsbyTopic.pending]:(state, action)=>{
      state.isLoading = true;
      state.hasError = false;
    },
    [fetchPostsbyTopic.fulfilled]:(state, action)=>{
      state.isLoading = false;
      state.hasError = false;
      state.posts = action.payload;
    },
    [fetchPostsbyTopic.rejected]:(state, action)=>{
      state.isLoading = false;
      state.hasError = true;
    }
  }

})



// creem selectors ca apoi sa folosim state si action in componenta(Posts.js) 
// acolo vom folosi useSelector(), useDispatch()
export const selectSelectedTopic =(state)=>state.selectedTopic;
export const selectFilteredSelectedTopic= (state) =>{
  const selectedTopic = selectSelectedTopic(state);
  return selectedTopic; 
}


export const selectPosts = (state)=>state.posts.posts;
/*
*/

export const selectFilteredPosts = createSelector(
  [selectPosts, selectSearchTerm],
  (posts, searchTerm) => {
    if (searchTerm !== '') {
      return posts.filter((post) =>
        post.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    return posts;
  }
);


// export action an reducer
export const {setSelectedTopic} = postsSlice.actions;
export default postsSlice.reducer;
